package stringEx;

public class TrimEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "  Sonal  ";
		
		System.out.println(s1+"Shinde"); //without trim()
		
		System.out.println("****************");
		
		System.out.println(s1.trim()+"Shinde"); //with trim()
	}

}
